import React from "react";
import { useParams } from 'react-router-dom';
import {useEffect, useState } from 'react';

import '../Page.css';
import { Link } from 'react-router-dom';

const Page = ()=>{
    const { id } = useParams();
    const [recettes,setRecette] = useState();

    useEffect(()=>{
        const getRecette = async ()=>{
          try {
            const res = await axios.get(`http://localhost:3001/api/v1/recettes/${id}`);
            setRecette(res.data);
            console.log('render');
          } catch (err) {
            console.log(err);
          }
        }
        getRecette();
      },[]);
    return(
        <>
            <main className="pages">
                <div className="left-page">
                    <img className="ime" src="/im1.jpg" alt="" />
                </div>
                <div className="right-page">
                    <a><Link to="/">retour</Link></a>
                    <h1>{recettes.nom} {id} </h1>
                    <h2>description</h2>
                    <p>
                        Lorem ipsum dolor sit amet consectetur, adipisicing elit. Maxime nihil quod ea ratione impedit, vel iusto et quam molestiae ipsum? Officiis adipisci molestiae, asperiores quidem numquam minus nemo velit corrupti?
                    </p>
                    <h2>ingredients</h2>
                    <p>
                        Lorem ipsum dolor sit amet consectetur, adipisicing elit. Maxime nihil quod ea ratione impedit, vel iusto et quam molestiae ipsum? Officiis adipisci molestiae, asperiores quidem numquam minus nemo velit corrupti?
                    </p>
                    <h2>instructions</h2>
                    <p>
                        Lorem ipsum dolor sit amet consectetur, adipisicing elit. Maxime nihil quod ea ratione impedit, vel iusto et quam molestiae ipsum? Officiis adipisci molestiae, asperiores quidem numquam minus nemo velit corrupti?
                    </p>
                    </div>
            </main>
        </>
    );
}

export default Page;
